import { Injectable, OnModuleInit } from '@nestjs/common';
import { EventListenService, EventListenerOn } from '@concepta/nestjs-events';
import { TargetEvent } from 'target-module';
class MyListener extends EventListenerOn<TargetEvent> {
  listen(event: TargetEvent) {
    console.log(event.payload);
  }
}
@Injectable()
class MyClass implements OnModuleInit {
constructor(private eventListenService: EventListenService) {}
onModuleInit() {
// listener instance
const listener = new MyListener();
// register the listener
this.eventListenService.on(TargetEvent, listener);
}
}

// event payload type
type MyEventPayload = {id: number, active: boolean};
// example event class
class MyEvent extends EventSync<MyEventPayload> {}
// example listener class
class MyListenOn extends EventListener<MyEvent>
  implements EventListenOnInterface
{
  // default options
  options = {};
  // custom handler
  listen(event: MyEvent): void {
    console.log(event.payload);
  }
}
// new listener
const myListener = new MyListener();
// subscribe to the event
EventListenService.on(MyEvent, myListener);
// and you can remove the listener easily
listener.remove();

